"use strict";

var inspect = require('../');

var test = require('tape');

test('circular', function (t) {
  t.plan(2);
  var obj = {
    a: 1,
    b: [3, 4]
  };
  obj.c = obj;
  t.equal(inspect(obj), '{ a: 1, b: [ 3, 4 ], c: [Circular] }');
  var _double = {};
  _double.a = [_double];
  _double.b = {};
  _double.b.inner = _double.b;
  _double.b.obj = _double;
  t.equal(inspect(_double), '{ a: [ [Circular] ], b: { inner: [Circular], obj: [Circular] } }');
});